@import "./base.scss";

.skills {
    position: relative;
    font-size: 34px;
    height: 300px;
    background: $background-light-blue;
    overflow: hidden;
    text-align: center;
    padding-top: 1.5rem;

    &__header {
        font-weight: 400;
    }

    &__text {
        position: absolute;
        white-space: nowrap;
        &--left,
        &--right {
            will-change: transform;
        }

        &--left {
            left: 0;
            top: 7rem;
           
            &.in-view {
                animation: moveLeft 15s linear infinite;
            }

            @keyframes moveLeft {
                0% {
                    transform: translate3d(0, 0, 0);
                }
                100% {
                    transform: translate3d(-50%, 0, 0);
                }
            }
        }
        &--right {
            right: 0;
            top: 12rem;

            &.in-view {
                animation: moveRight 15s linear infinite;
            }

            @keyframes moveRight {
                0% {
                    transform: translate3d(0, 0, 0);
                }
                100% {
                    transform: translate3d(50%, 0, 0);
                }
            }
        }
    }
    
    @media (max-width: 700px) {
        & {
            height: 15rem;
        }

        &__text {
            font-size: 20px;

            &--left {
                top: 7rem;
            }

            &--right {
                top: 10rem;
            }
        }
    }
}
