        transform: rotate(-20deg);
        transition: transform 0.5s cubic-bezier(0.585, 2.5, 0.645, 0.55);
    }

    &:hover:after {
        transform: translate3d(0, -5px, 0);
    }

    @media (max-width: 800px) {
        & {
            transform: scale3d(0.7, 0.7, 0.7);
            font-size: 23px;
        }
    }
}

.coloredIcon {
    fill: #fff;
    width: 35px;
    height: 35px;

    &:hover {
        animation: coloredIconAnimation 0.3s infinite;
    }

    @keyframes coloredIconAnimation {
        0% {
            filter: drop-shadow(5px 4px 2px $red-color);
        }

        25% {
            filter: drop-shadow(-5px -6px 2px $blue-color);
        }

        50% {
            filter: drop-shadow(5px -4px 2px $red-color);
        }

