                -o-transition-timing-function: cubic-bezier(.455,.03,.515,.955);
                transition-timing-function: cubic-bezier(.455,.03,.515,.955);
                -webkit-transition-duration: .1s;
                -o-transition-duration: .1s;
                transition-duration: .1s;
