        pointer-events: auto;
        cursor: pointer;
    }

    &:hover {
        background-color: #eeeeee;

        svg {
            fill: $blue-color;
        }
    }

    svg {
        width: 1rem;
        height: 1rem;
        fill: $accent-blue-color;
        transition: fill 0.5s ease-in-out;
